import React, { Component } from 'react';
import axios from 'axios';

export default class Donate extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeMessage = this.onChangeMessage.bind(this);
    this.onChangeAmount = this.onChangeAmount.bind(this);
    this.onChangeMedia_Url = this.onChangeMedia_Url.bind(this);
    this.onChangeStart_Time = this.onChangeStart_Time.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: '',
      message: '',
      amount: 0,
      media_url: '',
      start_time: 0
    }
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    })
  }

  onChangeMessage(e) {
    this.setState({
      message: e.target.value
    })
  }

  onChangeAmount(e) {
    this.setState({
      amount: e.target.value
    })
  }

  onChangeMedia_Url(e) {
    this.setState({
      media_url: e.target.value
    })
  }

  onChangeStart_Time(e) {
    this.setState({
      start_time: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const donation = {
      username: this.state.username,
      message: this.state.message,
      amount: this.state.amount,
      media_url: this.state.media_url,
      start_time: this.state.start_time
    }

    console.log(donation);

    axios.post('http://localhost:5000/donations/add', donation)
      .then(res => console.log(res.data));

    window.location = '/';
  }


  render() {
    return (
    <div className="container donation-form">
      <h2>Donate</h2>
      <p>Thank you for supporting the stream! All tips are non-refundable. Please only tip if you have the means to do so.</p>
      <hr/>
      <form>
          <div className="form-group">
            <label for="username">Your Username</label>
            <input
              required 
              type="text" 
              value={this.state.username} 
              onChange={this.onChangeUsername} 
              placeholder="Username" 
              className="form-control" 
              id="username" 
            />
          </div>
          <div className="form-group">
              <label for="amount">Tip Amount ($)</label>
              <input 
                required 
                type="number" 
                value={this.state.amount} 
                onChange={this.onChangeAmount} 
                placeholder="Your tip amount" 
                className="form-control" 
                id="amount" 
              />
            </div>
          <div className="form-group">
            <label for="message">Message</label>
            <textarea 
              value={this.state.message} 
              onChange={this.onChangeMessage} 
              rows="2" 
              maxLength="255" 
              placeholder="Your message..." 
              className="form-control" 
              id="message">
            </textarea>
          </div>
          <div className="form-row">
              <div className="form-group col-md-8">
                  <label for="media_url">Share Media</label>
                  <input 
                    type="url" 
                    value={this.state.media_url} 
                    onChange={this.onChangeMedia_Url} 
                    placeholder="YouTube Video URL..." 
                    className="form-control" 
                    id="media_url" 
                  />
              </div>
              <div className="form-group col-md-4">
                  <label for="start_time">Start Time</label>
                  <input 
                    type="time" 
                    value={this.state.start_time} 
                    onChange={this.onChangeStart_Time} 
                    className="form-control" 
                    id="start_time" 
                  />
              </div>
          </div>
          <button type="submit" className="btn btn-primary">Donate</button>
        </form>
    </div>
    )
  }
}
