import React, { Component } from 'react';
import axios from 'axios';
import '../alertbox.css';
import Speech from 'speak-tts';
import ReactPlayer from 'react-player';
import Donation_Alert from '../donation-alert.mp3'

export default class Alertbox extends Component {

    constructor(props) {
        super(props);

        this.state = {
            loaded: false
        };
    }

    componentDidMount() {
        axios.get('http://localhost:5000/donations/queue')
            .then(response => {
            this.setState({ donations: response.data, loaded: true })
        })
        .catch((error) => {
            console.log(error);
        })

        const script = document.createElement("script");
        script.src = "https://code.responsivevoice.org/responsivevoice.js?key=ltjeMrfo";
        script.async = true;
        script.onload = () => this.scriptLoaded();

        document.body.appendChild(script);
    }

    componentDidUpdate() {
        const donation = this.state.donations[0];
        const username = donation.username;
        const message = donation.message;
        const amount = donation.amount;
        const media = donation.media_url;
        const start_time = donation.start_time;

        const donation_alert_file = document.getElementsByClassName("donation-alert")[0];
        donation_alert_file.play();

        const speech = new Speech();

        speech.setLanguage('en-GB');
        speech.setPitch(1);
        speech.setRate(0.8);
        
        setTimeout(() => speech.speak({ text: `${username} donated $${amount} ${message}`}), 5800);

    }

                /*
            <ReactPlayer
                url='https://www.youtube.com/watch?v=ysz5S6PUM-U'
                config={{
                    youtube: {
                    playerVars: { start: 60, end: 70 }
                    }
                }}
                playing
            />
            */


    render() {
        return (
            <div>
                { this.state.loaded ? 
                <ReactPlayer
                    url={this.state.donations[0].media_url}
                    config={{
                        youtube: {
                        playerVars: { start: this.state.donations[0].start_time, end: this.state.donations[0].start_time + 30 }
                        }
                    }}
                    playing
                />
                : <p>Loading..</p> }

                <p>{this.state.loaded ?  this.state.donations[0].username : 'Loading..'}</p>
                <audio className="donation-alert">
                    <source src={Donation_Alert}></source>
                </audio>
                <div id="alert">
                <div className="donation"><span className="alert-color">Username</span> donated <span className="alert-color">$3</span>!</div>
                <div className="message">Hey, whats going on</div>
                </div>
                <div id="player"></div>
            </div>
        );

    }

    
}
