import React, { Component } from 'react';
import axios from 'axios';
import '../alertbox.css';

export default class Alertbox extends Component {

    constructor(props) {
        super(props);
    }

    componentDidMount() {
        axios.get('http://localhost:5000/donations/')
            .then(response => {
            this.setState({ donations: response.data })
            console.log(this.state.donations);
        })
        .catch((error) => {
            console.log(error);
        })
        
        var googleTTS = require('google-tts-api');
 
        googleTTS('Hello World', 'en', 1)   // speed normal = 1 (default), slow = 0.24
        .then(function (url) {
          console.log(url); // https://translate.google.com/translate_tts?...
        })
        .catch(function (err) {
          console.error(err.stack);
        });
    }

    render() {
        return (
        <div>
            <div id="alert">
                <div className="donation"><span className="alert-color">Username</span> donated <span className="alert-color">$3</span>!</div>
                <div className="message">Hey, whats going on</div>
            </div>
            <div id="player"></div>
        </div>
        );
    }
}
