import React, { Component } from 'react';
import axios from 'axios';
import '../alertbox.css';
import Speech from "speak-tts";

export default class Alertbox extends Component {

    constructor(props) {
        super(props);
    }

    componentDidMount() {
        axios.get('http://localhost:5000/donations/')
            .then(response => {
            this.setState({ donations: response.data })
            console.log(this.state.donations);
        })
        .catch((error) => {
            console.log(error);
        })
        test();
    }


    test = () => {
        const speech = new Speech()
        speech.init().then((data) => {
            // The "data" object contains the list of available voices and the voice synthesis params
            console.log("Speech is ready, voices are available", data)
        }).catch(e => {
            console.error("An error occured while initializing : ", e)
        })

        speech.speak({
            text: 'Hello, how are you tody ?',
        }).then(() => {
            console.log("Success !")
        }).catch(e => {
            console.error("An error occurred :", e)
        })
    }



    




    render() {
        return (
        <div>
            <div id="alert">
                <div className="donation"><span className="alert-color">Username</span> donated <span className="alert-color">$3</span>!</div>
                <div className="message">Hey, whats going on</div>
            </div>
            <div id="player"></div>
        </div>
        );
    }
}
