import React, { Component } from 'react';
import axios from 'axios';
import '../alertbox.css';

export default class Alertbox extends Component {

    constructor(props) {
        super(props);
    }

    componentDidMount() {
        axios.get('http://localhost:5000/donations/')
            .then(response => {
            this.setState({ donations: response.data })
            console.log(this.state.donations);
        })
        .catch((error) => {
            console.log(error);
        })

        const script = document.createElement("script");

        script.src = "https://code.responsivevoice.org/responsivevoice.js?key=ltjeMrfo";
        script.async = true;
    
        document.body.appendChild(script);
    }

    render() {
        return (
        <div>
            <div id="alert">
                <div className="donation"><span className="alert-color">Username</span> donated <span className="alert-color">$3</span>!</div>
                <div className="message">{window.responsiveVoice.speak('Hey, whats going on')}</div>
            </div>
            <div id="player"></div>
        </div>
        );
    }
}
