import React, { Component } from 'react';
import axios from 'axios';
import '../alertbox.css';
import Speech from 'speak-tts';
import ReactPlayer from 'react-player';
import Donation_Alert from '../donation-alert.mp3'

export default class Alertbox extends Component {

    constructor(props) {
        super(props);

        this.state = {
            loaded: false,
            tts_finished: false
        };
    }

    componentDidMount() {
        setInterval(() => { 
            axios.get('http://localhost:5000/donations/queue')
            .then(response => {
            this.setState({ donations: response.data, loaded: true })
            console.log('test');
            })
            .catch((error) => {
                console.log(error);
            });
        }, 3000);
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if(prevState.donations != this.state.donations){
            const donation = this.state.donations[0];
            const username = donation.username;
            const message = donation.message;
            const amount = donation.amount;
    
            const donation_alert_file = document.getElementsByClassName("donation-alert")[0];
            donation_alert_file.play();
    
            const speech = new Speech();
    
            speech.setLanguage('en-GB');
            speech.setPitch(1);
            speech.setRate(0.8);
    
            
            setTimeout(() => speech.speak({
                 text: `${username} donated $${amount} ${message}`,
                 listeners: {
                     onend: () => {
                         this.setState({ tts_finished: true });
                     }
                 }
            }), 5800);
        }
    }

    render() {
        return (
            <div>
                { this.state.tts_finished && this.state.donations[0].media_url ? 
                <ReactPlayer
                    url={this.state.donations[0].media_url} 
                    width='0'
                    height='0' 
                    config={{
                        youtube: {
                        playerVars: { start: this.state.donations[0].start_time, end: this.state.donations[0].start_time + 30 }
                        }
                    }}
                    playing
                />
                : <div></div>
                }

                {this.state.loaded ? 
                <div>
                    <audio className="donation-alert">
                        <source src={Donation_Alert}></source>
                    </audio>
                    <div id="alert">
                    <div className="donation"><span className="alert-color">{this.state.donations[0].username}</span> donated <span className="alert-color">${this.state.donations[0].amount}</span>!</div>
                    <div className="message">{this.state.donations[0].message}</div>
                    </div>
                    <div id="player"></div>
                </div>
                : <div></div>
                }

            </div>
        );

    }

    
}
