import React, { Component } from 'react';
import axios from 'axios';
import '../alertbox.css';
import Speech from 'speak-tts';
import ReactPlayer from 'react-player';
import Donation_Alert from '../donation-alert.mp3'

export default class Alertbox extends Component {

    constructor(props) {
        super(props);

        this.state = {
            loaded: false
        };
    }

    componentDidMount() {
        axios.get('http://localhost:5000/donations/queue')
            .then(response => {
            this.setState({ donations: response.data, loaded: true })
            
        })
        .catch((error) => {
            console.log(error);
        })

        if(this.state.loaded){
            console.log(this.state.donations);
        }
    }

    
    playDonationAlert() {
        /*
        const donation = this.state.donations[0];
        const username = donation.username;
