import React, { Component } from 'react';
import axios from 'axios';
import '../alertbox.css';
import Speech from 'speak-tts'
import ReactPlayer from 'react-player'

export default class Alertbox extends Component {

    constructor(props) {
        super(props);
    }


    componentDidMount() {
        axios.get('http://localhost:5000/donations/')
            .then(response => {
            this.setState({ donations: response.data })
            console.log(this.state.donations);
        })
        .catch((error) => {
            console.log(error);
        })        
        const speech = new Speech();

        speech.setLanguage('en-GB');
        speech.setPitch(1);
        speech.setRate(0.8);

        speech.speak({
            text: 'Hello, how are you today ?',
        }).then(() => {
            console.log("Success !")
        }).catch(e => {
            console.error("An error occurred :", e)
        })

    }

    render() {
        return (
        <div>
            <ReactPlayer url='https://www.youtube.com/watch?v=ysz5S6PUM-U' volume="0.1" width="0" height="0" playing />
            <div id="alert">
            <div className="donation"><span className="alert-color">Username</span> donated <span className="alert-color">$3</span>!</div>
            <div className="message">Hey, whats going on</div>
            </div>
            <div id="player"></div>
        </div>
        );
    }

    
}
